import React, { Component } from "react";
import { pieces } from "../../constants/action.types";
class Cell extends Component {
  constructor() {
    super();
    this.state = {
      class_css_o: "not-focusable btn btn-default btnO btn-piece",
      class_css_x: "not-focusable btn btn-default btnX btn-piece",
      class_css_normal: "not-focusable btn btn-default btn-piece"
    };
  }
  render() {
    const { data, row, col, tick, piecesWin, cellPerRow } = this.props;
    const { class_css_normal, class_css_o, class_css_x } = this.state;
    let my_class_css =
      data == pieces.O
        ? class_css_o
        : data == pieces.X
        ? class_css_x
        : class_css_normal;
    if (piecesWin != null) {
      for (var i = 0; i < piecesWin.length; i++) {
        console.log('winning?',{data, row, col, tick, piecesWin })
        if (piecesWin[i][0] == row && piecesWin[i][1] == col){
          my_class_css = my_class_css.concat(" btn-win");}
      }
    }

    return (
      <button
        className={my_class_css}
        onClick={() => {
          if (data == null) tick(row, col);
        }}
        style={{opacity: data ? 1 : 0.5}}
      >
        {data? data : (col)+ + 1+ cellPerRow*row}
      </button>
    );
  }
}
export default Cell;
