import React, { useState } from "react";
import { connect } from "react-redux";
import { bindActionCreators } from "redux";
import Home from "../components/home";
import * as home_actions from "../actions/home.action";
import { pieces } from "../constants/action.types";
import { isWin } from "../algorithm/main";

const HomeContainer = (props) => {
  const { actions, array_board, piece_current, number_cell } = props

  const [count, setCount] = useState(0)
  const [isWin, setWin] = useState(-1)
  const [piecesWin, setPiecesWin] = useState(null)

  const tick= (row, col) => {
    const { actions, array_board, piece_current, number_cell } = this.props;
    if (isWin === 1) {
      return;
    }
    // count number of tick 
    let count_tmp = count + 1;
    setCount(count_tmp );

    // update board 
    let newArray = [...array_board];
    newArray[row][col] = piece_current;
    actions.tick(newArray);

    // check win 
    const updatedPiecesWin = isWin(newArray, row, col, piece_current);
    if (updatedPiecesWin.length > 0) {
      setWin(1)
      setPiecesWin(updatedPiecesWin)
    } else if (count_tmp ==  number_cell * number_cell) {
      setWin(0)
    } else {
      // switch player
      actions.switch_piece(piece_current == pieces.X ? pieces.O : pieces.X);
    }
  }

  const reset_map = () => {
    actions.init_array(
      Array(number_cell)
        .fill(null)
        .map(() => Array(number_cell).fill(null))
    );
    actions.switch_piece(pieces.X);
    setCount(0)
    setWin(-1)
    setPiecesWin(null)
  }

  return (
    <Home
      setNumberCell={numberCell => {
        const number_cell = parseInt(numberCell);
        actions.set_number_cell(number_cell);
        actions.init_array(
          Array(number_cell)
            .fill(null)
            .map(() => Array(number_cell).fill(null))
        );
      }}
      array_board={array_board}
      tick={(row, col) => tick(row, col)}
      piece_current={piece_current}
      is_win={isWin}
      pieces_win={piecesWin}
      reset_map={() => reset_map()}
    />
  );
}

export default HomeContainer
