import React, { useState, useEffect } from "react";
import { Wheel } from "react-custom-roulette";
import xLogo from '../../img/x.png'

const Roulette = (props) => {
    const data = [
        { option: '0', image: {uri: xLogo}, text: 'Steak' },
        { option: '1', image: {uri: xLogo}, text: 'Chicken' },
        { option: '1', image: {uri: xLogo}, text: 'Fish' },
        { option: '1', image: {uri: xLogo}, text: 'Broccoli' },
        { option: '1', image: {uri: xLogo}, text: 'Cheese' },
        { option: '1', image: {uri: xLogo}, text: 'Egg' },
        { option: '1', image: {uri: xLogo}, text: 'Sugar' },
        { option: '1', image: {uri: xLogo}, text: 'Salt' },
        { option: '1', image: {uri: xLogo}, text: 'Milk' },
        { option: '1', image: {uri: xLogo}, text: 'Onion' },
        { option: '1', image: {uri: xLogo}, text: 'Lemon' },
        { option: '1', image: {uri: xLogo}, text: 'Flour' },
        { option: '1', image: {uri: xLogo}, text: 'Oil' },
        { option: '1', image: {uri: xLogo}, text: 'Tomato' },
        { option: '1', image: {uri: xLogo}, text: 'Pork' },
        { option: '1', image: {uri: xLogo}, text: 'Strawberry' },
        { option: '1', image: {uri: xLogo}, text: 'Potatoes' },
        { option: '1', image: {uri: xLogo}, text: 'Sushi' },
        { option: '1', image: {uri: xLogo}, text: 'Mushroom' },
        { option: '1', image: {uri: xLogo}, text: 'Shrimp' },
        { option: '1', image: {uri: xLogo}, text: 'Chocolate' },
      ]
  const [mustSpin, setMustSpin] = useState(false);
  const [prizeNumber, setPrizeNumber] = useState(0);
  console.log("ðŸš€ ~ Roulette ~ prizeNumber:", prizeNumber)
  const [rouletteData, setRouletteData] = useState(data);

  const handleSpinClick = () => {
    const newPrizeNumber = Math.floor(Math.random() * data.length);
    setPrizeNumber(newPrizeNumber);
    setMustSpin(true);
  };

  return (
    <>
      <div align="center" className="roulette-container">
        <Wheel
          mustStartSpinning={mustSpin}
          spinDuration={[0.2]}
          prizeNumber={prizeNumber}
          data={rouletteData}
          outerBorderColor={["#ccc"]}
          outerBorderWidth={[9]}
          innerBorderColor={["#f2f2f2"]}
          radiusLineColor={["tranparent"]}
          radiusLineWidth={[1]}
          textColors={["#f5f5f5"]}
          textDistance={90}
          fontSize={[4]}
          backgroundColors={[
            "#3f297e",
            "#175fa9",
            "#169ed8",
            "#239b63",
            "#64b031",
            "#efe61f",
            "#f7a416",
            "#e6471d",
            "#dc0936",
            "#e5177b",
            "#be1180",
            "#871f7f",
            "#3f297e",
            "#175fa9",
            "#169ed8",
            "#239b63",
            "#64b031",
            "#efe61f",
            "#f7a416",
            "#e6471d",
            "#dc0936",
            "#e5177b",
            "#be1180",
            "#871f7f"
          ]}
          onStopSpinning={() => {
            setMustSpin(false);
          }}
        />
        <button className="button roulette-button" onClick={handleSpinClick}>
          Spin
        </button>
      </div>
      <br />
      <button
        className="prize-message"
        onClick={handleSpinClick}
        disabled={mustSpin}
      >
        {!mustSpin ? rouletteData[prizeNumber].completeOption : "Spinning..."}
      </button>
    </>
  );
};

export default Roulette;
