import React, { useState, useEffect } from "react";
import { Wheel } from "react-custom-roulette";
import xLogo from '../../img/x.png'

const Roulette = (props) => {
    const data = [
        { option: 'Steak' },
        { option: 'Chicken' },
        { option: 'Fish' },
        { option: 'Broccoli' },
        { option: 'Cheese' },
        { option: 'Egg' },
        { option: 'Sugar' },
        { option: 'Salt' },
        { option: 'Milk' },
        { option: 'Onion' },
        { option: 'Lemon' },
        { option: 'Flour' },
        { option: 'Oil' },
        { option: 'Tomato' },
        { option: 'Pork' },
        { option: 'Strawberry' },
        { option: 'Potatoes' },
        { option: 'Sushi' },
        { option: 'Mushroom' },
        { option: 'Shrimp' },
        { option: 'Chocolate' },
      ]
  const [mustSpin, setMustSpin] = useState(false);
  const [prizeNumber, setPrizeNumber] = useState(0);
  console.log("ðŸš€ ~ Roulette ~ prizeNumber:", prizeNumber)
  const [rouletteData, setRouletteData] = useState(data);

  const handleSpinClick = () => {
    const newPrizeNumber = Math.floor(Math.random() * data.length);
    setPrizeNumber(newPrizeNumber);
    setMustSpin(true);
  };

  return (
    <>
      <div align="center" className="roulette-container">
        <Wheel
          mustStartSpinning={mustSpin}
          spinDuration={[0.2]}
          prizeNumber={prizeNumber}
          data={rouletteData}
          outerBorderColor={["#f2f2f2"]}
          outerBorderWidth={[9]}
          innerBorderColor={["#f2f2f2"]}
          innerBorderWidth={[9]}
          radiusLineColor={["#38393b"]}
        //   radiusLineColor={["transparent"]}
          radiusLineWidth={[2]}
          innerRadius={30}
          textColors={[
            // "#38383a", 
          "#f5f5f5", 
        ]}
          textDistance={70}
          fontSize={[15]}
          backgroundColors={[
            //   "#38393b",
            //   "#dbd5d5",
              "#f36e65",
            //   "#f59692",
            "#fbd4d7"
            //   "#ffffff",
            //   "#fddda2",
            // "#3f297e",
            // "#175fa9",
            // "#169ed8",
            // "#239b63",
            // "#64b031",
            // "#efe61f",
            // "#f7a416",
            // "#e6471d",
            // "#dc0936",
            // "#e5177b",
            // "#be1180",
            // "#871f7f",
            // "#3f297e",
            // "#175fa9",
            // "#169ed8",
            // "#239b63",
            // "#64b031",
            // "#efe61f",
            // "#f7a416",
            // "#e6471d",
            // "#dc0936",
            // "#e5177b",
            // "#be1180",
            // "#871f7f"
          ]}
          onStopSpinning={() => {
            setMustSpin(false);
          }}
        />
        <button className="button roulette-button" onClick={handleSpinClick}>
          Spin
        </button>
      </div>
      <br />
      {/* <button
        className="prize-message"
        onClick={handleSpinClick}
        disabled={mustSpin}
      >
        {!mustSpin ? rouletteData[prizeNumber].completeOption : "Spinning..."}
      </button> */}
    </>
  );
};

export default Roulette;
