import React, {useState} from "react";
import { TextField } from "@material-ui/core";

const Header = () => {
  const [player1, setPlayer1] = useState('')
  const [player2, setPlayer2] = useState('')
  const handleNameChange = (e) => type => {
    console.log("ðŸš€ ~ handleNameChange ~ type:", type)
    if (type === 'player1'){
      setPlayer1(e.target.value)
    } else if (type === 'player2'){
      setPlayer2(e.target.value)
    }
  }
    return (
      <nav className="navbar navbar-expand-sm navbar-light bg-faded">
        <button
          className="navbar-toggler"
          type="button"
          data-toggle="collapse"
          data-target="#nav-content"
          aria-controls="nav-content"
          aria-expanded="false"
          aria-label="Toggle navigation"
        >
          <span className="navbar-toggler-icon" />
        </button>
        <TextField required id="standard-required" label="Player 1" value={player1} onChange={(e) => handleNameChange(e)('player1')}/>
        <TextField required id="standard-required" label="Player 2" value={player2} onChange={(e) => handleNameChange(e)('player2')}/>
        <span className="navbar-brand">Tic-Tac-Toe</span>
      </nav>
    );
  }

export default Header;
