import React, {useState} from 'react';
import AppBar from '@material-ui/core/AppBar';
import Button from '@material-ui/core/Button';
import Card from '@material-ui/core/Card';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';
import Grid from '@material-ui/core/Grid';
import Toolbar from '@material-ui/core/Toolbar';
import Typography from '@material-ui/core/Typography';
import { makeStyles } from '@material-ui/core/styles';
import Link from '@material-ui/core/Link';
import Header from "./common/header";
import Row from "./board/row";
import { TextField, Container, CssBaseline, Paper } from "@material-ui/core";

function Copyright() {
  return (
    <Typography variant="body2" color="textSecondary" align="center">
      {'Copyright Â© '}
      <Link color="inherit" href="https://mui.com/">
        Your Website
      </Link>{' '}
      {new Date().getFullYear()}
      {'.'}
    </Typography>
  );
}

const useStyles = makeStyles((theme) => ({
  icon: {
    marginRight: theme.spacing(2),
  },
  heroContent: {
    backgroundColor: "#f59692",
    padding: theme.spacing(8, 0, 6),
  },
  heroButtons: {
    marginTop: theme.spacing(4),
  },
  cardGrid: {
    paddingTop: theme.spacing(8),
    paddingBottom: theme.spacing(8),
  },
  card: {
    height: '100%',
    display: 'flex',
    flexDirection: 'column',
  },
  cardMedia: {
    paddingTop: '56.25%', // 16:9
  },
  cardContent: {
    flexGrow: 1,
  },
  footer: {
    backgroundColor: theme.palette.background.paper,
    padding: theme.spacing(6),
  },
  root: {
    "& .MuiPaper-root": {
      borderRadius: "20px",
      border: 'thin solid black',
      boxShadow: 'none',
      padding: 20
    },
    '& .MuiAppBar': {
      backgroundColor: 'red'
    }
  }
}));

const Home = (props) =>  {
    const {
      setNumberCell,
      arrayBoard,
      tick,
      currentPiece,
      isWin,
      piecesWin,
      resetMap
    } = props;

  const classes = useStyles();
  const [boardState, setBoardState] = useState({
      numbercell: null,
      show: false
  })

  return (
    <React.Fragment>
      <CssBaseline />
      <AppBar position="relative" color="transparent">
        <Toolbar>
        <Header />
        </Toolbar>
      </AppBar>
      <main className={classes.root}>
        <div className={classes.heroContent}>
        <Container component="main" maxWidth="lg" sx={{ mb: 4 }}>

           <Paper>
           {arrayBoard === null ? (
            <>
              <div className="input-custom d-flex align-items-center justify-content-center">
                <p className="mb-0 me-3">
                  Board Size <br />
                  Min 5 - Max 20 <br />
                </p>
                <TextField 
                  type="number"
                  required
                  onChange={(event) => {
                    setBoardState({ ...boardState, numbercell: event.target.value });
                  }}
                  onKeyPress={(event) => {
                    if (event.key === "Enter") {
                      if (
                        boardState.numbercell === null ||
                        (!Number.isInteger(boardState.numbercell) &&
                          (parseInt(boardState.numbercell) < 5 ||
                            parseInt(boardState.numbercell) > 20))
                      ) {
                        alert("Invalid data!");
                        return;
                      }


                      setNumberCell(boardState.numbercell);
                    }
                  }}
                />
              </div>
            </>
          ) : (
            <Grid container direction="column" alignContent="center">
              <Grid item>
                <p className="title">INFORMATION</p>
                  <p>ACTIVE: {currentPiece}</p>
                  <p>
                    {" "}
                    Result :{" "}
                    {isWin === 1 ? (
                      <span>{currentPiece} WIN</span>
                    ) : isWin === 0 ? (
                      <span>DRAW</span>
                    ) : null}
                  </p>
                  <button onClick={() => resetMap()} className="btn btn-info">
                    Reset
                  </button>
              </Grid>
              <Grid item style={{border: '0.5px black solid', borderRadius: 2}}>
                {arrayBoard.map((e, index) => {
                    return (
                      <div >
                        <Row
                          elements={e}
                          tick={(row, col) => tick(row, col)}
                          row={index}
                          piecesWin={piecesWin} />
                      </div>
                    );
                  })}
              </Grid>
            </Grid>
            // <div className="row board">
            //   <div className="col-md-10">
            //     {arrayBoard.map((e, index) => {
            //       return (
            //         <div>
            //           <Row
            //             elements={e}
            //             tick={(row, col) => tick(row, col)}
            //             row={index}
            //             piecesWin={piecesWin} />
            //         </div>
            //       );
            //     })}
            //   </div>
            //   <div className="col-md-2 information">
            //     <p className="title">INFORMATION</p>
            //     <p>ACTIVE: {currentPiece}</p>
            //     <p>
            //       {" "}
            //       Result :{" "}
            //       {isWin === 1 ? (
            //         <span>{currentPiece} WIN</span>
            //       ) : isWin === 0 ? (
            //         <span>DRAW</span>
            //       ) : null}
            //     </p>
            //     <button onClick={() => resetMap()} className="btn btn-info">
            //       Reset
            //     </button>
            //   </div>
            // </div>
          )}
          </Paper>
        </Container>
        </div>
      </main>
      {/* Footer */}
      <footer className={classes.footer}>
        <Typography variant="h6" align="center" gutterBottom>
          Footer
        </Typography>
        <Typography variant="subtitle1" align="center" color="textSecondary" component="p">
          Something here to give the footer a purpose!
        </Typography>
        <Copyright />
      </footer>
      {/* End footer */}
    </React.Fragment>
  );
}

export default Home
