import { useState } from "react";
import { pieces } from "../constants/action.types";
import { isWinning } from "../algorithm/main";
import { valentines, news, code } from "../questions";

const usePlayers = () => {
  const [player1, setPlayer1] = useState('')
  const [player2, setPlayer2] = useState('')
  const handleNameChange = (e) => type => {
    if (type === 'player1'){
      setPlayer1(e.target.value)
    } else if (type === 'player2'){
      setPlayer2(e.target.value)
    }
  }
  return {
    player1,
    player2,
    handleNameChange
  }
}

export const usePlayerItems = (props) => {
  const [player1Items, setPlayer1Items] = useState([])
  const [player2Items, setPlayer2Items] = useState([])

  const handlePlayerItems = ({player, item}) => {
    console.log("ðŸš€ ~ handlePlayerItems ~ {player, item}:", {player, item})
    const fn = player === 'player1' ? setPlayer1Items : setPlayer2Items
    const items = player === 'player1' ? player1Items : player2Items

    fn([...items, item])
  } 

  return {
    player1Items,
    player2Items,
    handlePlayerItems
  }
}

export const useHomePage = () => {
  const [numberCell, setNumberCell] = useState(null)
  const [arrayBoard, setArrayBoard] = useState(null)
  const [currentPiece, setCurrentPiece] = useState(pieces.X)
  const [dialogState, setDialogState] = useState({
    open: false,
    title: '',
    content: '',
    currentPiece: 'X'
  })

  const {    player1Items,
    player2Items,
    handlePlayerItems } = usePlayerItems()

  const {
    player1,
    player2,
    handleNameChange
  } = usePlayers()
  const onSetNumberCell = numberCell => {
    setNumberCell(parseInt(numberCell))
  }

const onInitArray = arrayBoard => {
  setArrayBoard(arrayBoard)
}

const onTick =  (array_new) => {
  setArrayBoard(array_new)
}

const onSwitchPiece = (data) => {
  setCurrentPiece(data)
}

const [count, setCount] = useState(0)
  const [isWin, setWin] = useState(-1)
  const [piecesWin, setPiecesWin] = useState(null)

  let haveIt = [];

const generateUniqueRandom = (maxNr) => {
    //Generate random number
    // let random = (Math.random() * maxNr).toFixed();

    // //Coerce to number by boxing
    // random = Number(random);
    let random =  Math.random() | 0

    if(!haveIt.includes(random)) {
        haveIt.push(random);
        return random;
    } else {
        if(haveIt.length < maxNr) {
          //Recursively generate number
         return  generateUniqueRandom(maxNr);
        } else {
          console.log('No more numbers available.')
          return false;
        }
    }
}

  const handleDialog = ({open, title, currentPiece}) => {
    const list = [...valentines, ...news, ...code]
    const item = list[list.length * Math.random() | 0]
    const content = item.question
    const answer = item.answer
    const img = item.img
    console.log("ðŸš€ ~ handleDialog ~ img:", img)
    setDialogState({open , content, title, answer, currentPiece, img})
  }
  const tick= (row, col) => {
    if (isWin === 1) {
      return;
    }

    let count_tmp = count + 1;
    setCount(count_tmp );

    // update board 
    let newArray = [...arrayBoard];
    newArray[row][col] = currentPiece;
    onTick(newArray);

    // check win 
    const updatedPiecesWin = isWinning(newArray, row, col, currentPiece);
    if (updatedPiecesWin.length > 0) {
      setWin(1)
      setPiecesWin(updatedPiecesWin)

    } else if (count_tmp ===  numberCell * numberCell) {
      setWin(0)
    } else {
      // switch player
      onSwitchPiece(currentPiece === pieces.X ? pieces.O : pieces.X);
      const currentPlayer = currentPiece === pieces.X ? 'Player 1' : 'Player 2'
      const title = `${currentPlayer}'s turn`
      handleDialog({open: true, title, currentPiece: currentPiece})
    }
  }

  const resetMap = () => {
    onInitArray(
      Array(numberCell)
        .fill(null)
        .map(() => Array(numberCell).fill(null))
    );
    onSwitchPiece(pieces.X);
    setCount(0)
    setWin(-1)
    setPiecesWin(null)
  }



  return {
    numberCell,
    setNumberCell:(val) => {
      const result = parseInt(val);
      onSetNumberCell(result);
      onInitArray(
        Array(result)
          .fill(null)
          .map(() => Array(result).fill(null))
      );
    },
    currentPiece,
    arrayBoard,
    onSetNumberCell,
    onInitArray,
    onTick,
    onSwitchPiece,
    tick, isWin, piecesWin, resetMap,
    handleDialog,
    dialogState,
    player1,
    player2,
    handleNameChange,
    player1Items,
    player2Items,
    handlePlayerItems
  }
}
