import React, {useState} from "react";
import { TextField, Grid } from "@material-ui/core";
import textLogo from '../../img/text_logo.png'
import heartLogo from '../../img/heart.png'
import xLogo from '../../img/x.png'

const Header = () => {
  const [player1, setPlayer1] = useState('')
  const [player2, setPlayer2] = useState('')
  const handleNameChange = (e) => type => {
    console.log("ðŸš€ ~ handleNameChange ~ type:", type)
    if (type === 'player1'){
      setPlayer1(e.target.value)
    } else if (type === 'player2'){
      setPlayer2(e.target.value)
    }
  }
    return (
      <Grid container justifyContent="space-evenly" spacing={4}>
        <Grid item  container md={4} alignItems="flex-end">
          <Grid item >
          <img src={xLogo} style={{maxWidth: 30}} alt="x"/>
          </Grid>
          <Grid item >
        <TextField required id="standard-required " label="Player 1" value={player1} onChange={(e) => handleNameChange(e)('player1')}/>
          </Grid>
        </Grid>
        <Grid item md={4}>
        <img src={textLogo} alt="logo" style={{maxWidth: 200, display: 'block', margin: 'auto'}} />
        </Grid>
        <Grid item container md={4} alignItems="center">
          <Grid item>
            <img src={heartLogo} style={{maxWidth: 30}} alt="heart"/>
          </Grid>
          <Grid item>
          <TextField required  id="standard-required" label="Player 2" value={player2} onChange={(e) => handleNameChange(e)('player2')}/>
          </Grid>
        </Grid>
      </Grid>
    );
  }

export default Header;
