import React, { useState, useEffect } from "react";
import { Wheel } from "react-custom-roulette";

const Roulette = ({currentPiece, handlePlayerItems}) => {
    // console.log("ðŸš€ ~ Roulette ~ {currentPiece, handlePlayerItems}:", {currentPiece, handlePlayerItems})
    const data = [
        { option: 'Steak' },
        { option: 'Chicken' },
        { option: 'Fish' },
        { option: 'Broccoli' },
        { option: 'Cheese' },
        { option: 'Egg' },
        { option: 'Sugar' },
        { option: 'Salt' },
        { option: 'Milk' },
        { option: 'Onion' },
        { option: 'Lemon' },
        { option: 'Flour' },
        { option: 'Oil' },
        { option: 'Tomato' },
        { option: 'Pork' },
        { option: 'Strawberry' },
        { option: 'Potatoes' },
        { option: 'Sushi' },
        { option: 'Mushroom' },
        { option: 'Shrimp' },
        { option: 'Chocolate' },
        { option: 'Garlic' },
      ]
  const [mustSpin, setMustSpin] = useState(false);
  const [prizeNumber, setPrizeNumber] = useState(0);
  const [rouletteData, setRouletteData] = useState(data);

  const handleSpinClick = () => {
    if (!mustSpin){
        const newPrizeNumber = Math.floor(Math.random() * data.length);
        setPrizeNumber(newPrizeNumber);
        setMustSpin(true);
        handlePlayerItems({player: currentPiece === 'X' ? 'player1' : 'player2', item: newPrizeNumber})
    }
  };

  return (
    <>
      <div align="center" className="roulette-container">
        <Wheel
          mustStartSpinning={mustSpin}
          spinDuration={[0.2]}
          prizeNumber={prizeNumber}
          data={rouletteData}
          outerBorderColor={["#fddda2"]}
          outerBorderWidth={[9]}
          innerBorderColor={["#fddda2"]}
          innerBorderWidth={[30]}
          radiusLineColor={["transparent"]}
          radiusLineWidth={[2]}
          textColors={[
            "#f5f5f5", 
            "#38383a", 
        ]}
          textDistance={70}
          fontSize={[15]}
          backgroundColors={[
              "#38393b",
              "#dbd5d5",
              "#f36e65",
              "#ffffff",
              "#f59692",
              "#fbd4d7",
          ]}
          onStopSpinning={() => {
            setMustSpin(false);
          }}
        />
        <button className="button roulette-button" onClick={handleSpinClick}>
          Spin
        </button>
      </div>
      <br />
    </>
  );
};

export default Roulette;
