import React, { Component } from "react";
import Header from "./common/header";
import Row from "./board/row";
class Home extends Component {
  constructor(props) {
    super(props);
    this.state = {
      numbercell: null,
      show: false
    };
  }

  render() {
    const {
      setNumberCell,
      array_board,
      tick,
      piece_current,
      isWin,
      piecesWin,
      reset_map
    } = this.props;
    return (
      <div>
        <div className="container">
          <Header />
          {array_board == null ? (
            <>
              <div className="input-custom d-flex align-items-center justify-content-center">
                <p className="mb-0 me-3">
                  Nhập kích thước bàn cờ <br />
                  Min 5 - Max 20 <br />
                </p>
                <input
                  type="number"
                  placeholder="CELL"
                  min="5"
                  max="20"
                  required
                  onChange={(event) => {
                    this.setState({ numbercell: event.target.value });
                  }}
                  onKeyPress={(event) => {
                    if (event.key == "Enter") {
                      if (
                        this.state.numbercell == null ||
                        (!Number.isInteger(this.state.numbercell) &&
                          (parseInt(this.state.numbercell) < 5 ||
                            parseInt(this.state.numbercell) > 20))
                      ) {
                        alert("Invalid data!");
                        return;
                      }

                      setNumberCell(this.state.numbercell);
                    }
                  }}
                />
              </div>
            </>
          ) : (
            <div className="row board">
              <div className="col-md-10">
                {array_board.map((e, index) => (
                  <div>
                    <Row
                      elements={e}
                      tick={(row, col) => tick(row, col)}
                      row={index}
                      piecesWin={piecesWin}
                    />
                  </div>
                ))}
              </div>
              <div className="col-md-2 information">
                <p className="title">INFORMATION</p>
                <p>ACTIVE: {piece_current}</p>
                <p>
                  {" "}
                  Result :{" "}
                  {isWin == 1 ? (
                    <span>{piece_current} WIN</span>
                  ) : isWin == 0 ? (
                    <span>DRAW</span>
                  ) : null}
                </p>
                <button onClick={() => reset_map()} className="btn btn-info">
                  Reset
                </button>
              </div>
            </div>
          )}
        </div>
      </div>
    );
  }
}

export default Home;
