import { useState } from "react";
import { types, pieces } from "../constants/action.types";

const initalState = {
  numberCell: null,
  arrayBoard: null,
  currentPiece: pieces.X
};


export const useHomePage = () => {
  const [numberCell, setNumberCell] = useState(null)
  const [arrayBoard, setArrayBoard] = useState(null)
  const [currentPiece, setCurrentPiece] = useState(pieces.X)

  const main = (state = initalState, action) => {
    let updatedState = state
    switch (action.type) {
      case types.SET_numberCell: {
        updatedState ={ ...state, numberCell: parseInt(action.numberCell) };
        break
      }
      case types.INIT_ARRAY: {
        updatedState ={ ...state, arrayBoard: action.arrayBoard };
        break
      }
      case types.SWITCH_PIECE: {
        updatedState ={ ...state, currentPiece: action.data };
        break
      }
      case types.TICK: {
        updatedState ={...state, arrayBoard: action.array_new}
        break
      }
      default:
        updatedState =state;
    }
  };


  const onSetNumberCell = numberCell => {
    setNumberCell(parseInt(numberCell))
  }

const onInitArray = arrayBoard => {
  setArrayBoard(arrayBoard)
}

const onTick =  (array_new) => {
  setArrayBoard(array_new)
}

const onSwitchPiece = (data) => {
  setCurrentPiece(data)
}

  return {
    numberCell,
    currentPiece,
    arrayBoard,
    onSetNumberCell,
    onInitArray,
    onTick,
    onSwitchPiece
  }
}
