import React, {useState} from 'react';
import { TextField, Grid, Typography, Collapse, ListItem, ListItemText, Dialog, DialogActions, DialogContent, DialogContentText, DialogTitle, Button } from "@material-ui/core";
import Wheel from './wheel'
import Card from './card'
import { recipes } from '../../constants/recipes';


const OverviewDialog = (props) => {
    console.log("ðŸš€ ~ OverviewDialog ~ props:", props)
    const {
        dialogState = {},
        handleDialog,
        showAnswer,
        setShowSpin,
        setShowAnswer,
        showSpin,
        handlePlayerItems,
        player1Items,
        player2Items    
    } = props
    console.log('hwee')
    return (
        <Dialog
        fullWidth
        open={dialogState.open}
        onClose={() => {
          handleDialog({open: false })
          setShowAnswer(false)
          setShowSpin(false)
        }}
        aria-labelledby="max-width-dialog-title"
        maxWidth="md"
      >
         <DialogTitle id="max-width-dialog-title">Menu</DialogTitle>
        <DialogContent>
          <Grid container>
          <Typography variant="body1" component={"div"}>Player 1</Typography>
          </Grid>
          <Grid container spacing={2}>
          {player1Items.map((item, i) => {
              const {option} = item
              return (
                <Grid item>
                  {option}
                </Grid>
              )
          })}
           </Grid>
           <Typography variant="body1" component={"div"}>Player 2</Typography>
           <Grid container spacing={2}>
           {player2Items.map((item, i) => {
              const {option} = item
              return (
                <Grid item>
                  {option}
                </Grid>
              )
          })}
           </Grid>
           <Grid container spacing={2}>
            {recipes.map((recipe, i) => {
              const {name, uri, ingredients} = recipe
              return (
                <Grid item key={i} xs={12} sm={6} md={2}>
                <Card
                  name={name}
                  uri={uri}
                  ingredients={ingredients}
                />
                </Grid>
              )
            })}
          </Grid>

        </DialogContent>
        <DialogActions>
          <Button onClick={() => {
            handleDialog({open: false}) 
            setShowAnswer(false)
            setShowSpin(false)
          }} 
            color="primary">
            Close
          </Button>
        </DialogActions>
      </Dialog>
    )
}

export default OverviewDialog
