import React, {useState} from 'react';
import AppBar from '@material-ui/core/AppBar';
import Grid from '@material-ui/core/Grid';
import Toolbar from '@material-ui/core/Toolbar';
import Typography from '@material-ui/core/Typography';
import { makeStyles } from '@material-ui/core/styles';
import Link from '@material-ui/core/Link';
import Header from "./common/header";
import Row from "./board/row";
import { TextField, Container, CssBaseline, Paper, Dialog, DialogActions, DialogContent, DialogContentText, DialogTitle, Button } from "@material-ui/core";

function Copyright() {
  return (
    <Typography variant="body2" color="textSecondary" align="center">
      {'Copyright © '}
      <Link color="inherit" href="https://mui.com/">
        Your Website
      </Link>{' '}
      {new Date().getFullYear()}
      {'.'}
    </Typography>
  );
}

const useStyles = makeStyles((theme) => ({
  icon: {
    marginRight: theme.spacing(2),
  },
  heroContent: {
    backgroundColor: "#f59692",
    padding: theme.spacing(8, 0, 6),
  },
  heroButtons: {
    marginTop: theme.spacing(4),
  },
  cardGrid: {
    paddingTop: theme.spacing(8),
    paddingBottom: theme.spacing(8),
  },
  card: {
    height: '100%',
    display: 'flex',
    flexDirection: 'column',
  },
  cardMedia: {
    paddingTop: '56.25%', // 16:9
  },
  cardContent: {
    flexGrow: 1,
  },
  footer: {
    backgroundColor: theme.palette.background.paper,
    padding: theme.spacing(6),
  },
  root: {
    "& .MuiPaper-root": {
      // borderRadius: "20px",
      // border: 'thin solid black',
      boxShadow: 'none',
      padding: 20
    },
    '& .MuiAppBar': {
      backgroundColor: '#fbd4d7'
    },
    '& .MuiTextField-root': {
      backgroundColor: 'white',
      // border: '1px solid black',
      // borderRadius: '15px'
    }
  }
}));

const Home = (props) =>  {
    const {
      setNumberCell,
      arrayBoard,
      tick,
      currentPiece,
      isWin,
      piecesWin,
      resetMap
    } = props;

  const classes = useStyles();
  const [boardState, setBoardState] = useState({
      numbercell: null,
      show: false
  })

  return (
    <React.Fragment>
      <main className={classes.root}>
      <CssBaseline />
      <AppBar position="relative" color="transparent" style={{backgroundColor: '#fbd4d7'}}>
        <Toolbar>
        <Header />
        </Toolbar>
      </AppBar>
        <div className={classes.heroContent}>
        <Container component="main" maxWidth="xlg" sx={{ mb: 4 }}>

           {/* <Paper> */}
           {arrayBoard === null ? (
            <>
              <div className="input-custom d-flex align-items-center justify-content-center">
                <TextField 
                  type="number"
                  required
                  label={"Board Size (5-20)"}
                  onChange={(event) => {
                    setBoardState({ ...boardState, numbercell: event.target.value });
                  }}
                  onKeyPress={(event) => {
                    if (event.key === "Enter") {
                      if (
                        boardState.numbercell === null ||
                        (!Number.isInteger(boardState.numbercell) &&
                          (parseInt(boardState.numbercell) < 5 ||
                            parseInt(boardState.numbercell) > 20))
                      ) {
                        alert("Invalid data!");
                        return;
                      }
                      setNumberCell(boardState.numbercell);
                    }
                  }}
                />
              </div>
            </>
          ) : (
            <Grid container direction="column" alignContent="center">
              <Grid item>
                <p className="title">INFORMATION</p>
                  <p>ACTIVE: {currentPiece}</p>
                  <p>
                    {" "}
                    Result :{" "}
                    {isWin === 1 ? (
                      <span>{currentPiece} WIN</span>
                    ) : isWin === 0 ? (
                      <span>DRAW</span>
                    ) : null}
                  </p>
                  <button onClick={() => resetMap()} className="btn btn-info">
                    Reset
                  </button>
              </Grid>
              <Grid item style={{border: '0.5px black solid', borderRadius: 5, backgroundColor: 'white'}}>
                {arrayBoard.map((e, index) => {
                    return (
                      <div >
                        <Row
                          elements={e}
                          tick={(row, col) => tick(row, col)}
                          row={index}
                          piecesWin={piecesWin} />
                      </div>
                    );
                  })}
              </Grid>
            </Grid>
            // <div className="row board">
            //   <div className="col-md-10">
            //     {arrayBoard.map((e, index) => {
            //       return (
            //         <div>
            //           <Row
            //             elements={e}
            //             tick={(row, col) => tick(row, col)}
            //             row={index}
            //             piecesWin={piecesWin} />
            //         </div>
            //       );
            //     })}
            //   </div>
            //   <div className="col-md-2 information">
            //     <p className="title">INFORMATION</p>
            //     <p>ACTIVE: {currentPiece}</p>
            //     <p>
            //       {" "}
            //       Result :{" "}
            //       {isWin === 1 ? (
            //         <span>{currentPiece} WIN</span>
            //       ) : isWin === 0 ? (
            //         <span>DRAW</span>
            //       ) : null}
            //     </p>
            //     <button onClick={() => resetMap()} className="btn btn-info">
            //       Reset
            //     </button>
            //   </div>
            // </div>
          )}
          {/* </Paper> */}
        </Container>
        <Dialog
        fullWidth
        open={true}
        onClose={() => {}}
        aria-labelledby="max-width-dialog-title"
      >
         <DialogTitle id="max-width-dialog-title">Optional sizes</DialogTitle>
        <DialogContent>
          <DialogContentText>
            You can set my maximum width and whether to adapt or not.
          </DialogContentText>
        </DialogContent>
        <DialogActions>
          <Button onClick={() => {}} color="primary">
            Close
          </Button>
        </DialogActions>
      </Dialog>
        </div>
      </main>
      {/* Footer */}
      <footer className={classes.footer}>
        <Typography variant="h6" align="center" gutterBottom>
          Footer
        </Typography>
        <Typography variant="subtitle1" align="center" color="textSecondary" component="p">
          Something here to give the footer a purpose!
        </Typography>
        <Copyright />
      </footer>
      {/* End footer */}
    </React.Fragment>
  );
}

export default Home
