import React, {useState} from 'react';
import { TextField, Grid, CssBaseline, Collapse, ListItem, ListItemText, Dialog, DialogActions, DialogContent, DialogContentText, DialogTitle, Button } from "@material-ui/core";
import Wheel from './wheel'
import Card from './card'
import { recipes } from '../../constants/recipes';


const OverviewDialog = (props) => {
    console.log("ðŸš€ ~ OverviewDialog ~ props:", props)
    const {
        dialogState = {},
        handleDialog,
        showAnswer,
        setShowSpin,
        setShowAnswer,
        showSpin,
        handlePlayerItems,
        player1Items,
        player2Items    
    } = props
    console.log('hwee')
    return (
        <Dialog
        fullWidth
        open={true}
        onClose={() => {
          handleDialog({open: false })
          setShowAnswer(false)
          setShowSpin(false)
        }}
        aria-labelledby="max-width-dialog-title"
        maxWidth="lg"
      >
         <DialogTitle id="max-width-dialog-title">Menu</DialogTitle>
        <DialogContent>
          <Grid container spacing={4}>
          {
            recipes.map((recipe, i) => {
              const {name, uri, ingredients} = recipe
              return (
                <Grid item key={i} xs={12} sm={6} md={3}>
                <Card
                  name={name}
                  uri={uri}
                  ingredients={ingredients}
                />
                </Grid>
              )
            })
          }
          </Grid>
        </DialogContent>
        <DialogActions>
          <Button onClick={() => {
            handleDialog({open: false}) 
            setShowAnswer(false)
            setShowSpin(false)
          }} 
            color="primary">
            Close
          </Button>
        </DialogActions>
      </Dialog>
    )
}

export default OverviewDialog
