import React, { useState } from "react";

import Home from "../components/home";
import * as actions from "../actions/home.action";
import { pieces } from "../constants/action.types";
import { isWinning } from "../algorithm/main";

const HomeContainer = (props) => {
  const { numberCell,
    currentPiece,
    arrayBoard,
    onSetNumberCell,
    onInitArray,
    onTick,
    onSwitchPiece } = props

  const [count, setCount] = useState(0)
  const [isWin, setWin] = useState(-1)
  const [piecesWin, setPiecesWin] = useState(null)

  const tick= (row, col) => {
    if (isWin === 1) {
      return;
    }
    // count number of tick 
    let count_tmp = count + 1;
    setCount(count_tmp );

    // update board 
    let newArray = [...arrayBoard];
    newArray[row][col] = currentPiece;
    onTick(newArray);

    // check win 
    const updatedPiecesWin = isWinning(newArray, row, col, currentPiece);
    console.log("ðŸš€ ~ tick ~ updatedPiecesWin:", updatedPiecesWin)
    if (updatedPiecesWin.length > 0) {
      console.log("ðŸš€ ~ tick ~ updatedPiecesWin.length:", updatedPiecesWin.length)
      setWin(1)
      setPiecesWin(updatedPiecesWin)
      console.log({isWin, piecesWin})
    } else if (count_tmp ==  numberCell * numberCell) {
      setWin(0)
    } else {
      // switch player
      onSwitchPiece(currentPiece == pieces.X ? pieces.O : pieces.X);
    }
  }

  const reset_map = () => {
    onInitArray(
      Array(numberCell)
        .fill(null)
        .map(() => Array(numberCell).fill(null))
    );
    onSwitchPiece(pieces.X);
    setCount(0)
    setWin(-1)
    setPiecesWin(null)
  }

  return (
    <Home
      setNumberCell={val => {
        const result = parseInt(val);
        onSetNumberCell(result);
        onInitArray(
          Array(result)
            .fill(null)
            .map(() => Array(result).fill(null))
        );
      }}
      arrayBoard={arrayBoard}
      tick={(row, col) => tick(row, col)}
      currentPiece={currentPiece}
      is_win={isWin}
      pieces_win={piecesWin}
      reset_map={() => reset_map()}
    />
  );
}

export default HomeContainer
