import React, { Component } from "react";
import Header from "./common/header";
import Row from "./board/row";
import { TextField, Container, CssBaseline } from "@material-ui/core";
class Home extends Component {
  constructor(props) {
    super(props);
    this.state = {
      numbercell: null,
      show: false
    };
  }

  render() {
    const {
      setNumberCell,
      arrayBoard,
      tick,
      currentPiece,
      isWin,
      piecesWin,
      resetMap
    } = this.props;
      console.log("ðŸš€ ~ Home ~ render ~ piecesWin:", piecesWin)

    return (
      <>
      <CssBaseline/>
      <div  style={{backgroundColor: "#f59692"}}>
      <Container component="main" maxWidth="sm" sx={{ mb: 4 }}>
        {/* <div className="container"> */}
          <Header />
          {arrayBoard == null ? (
            <>
              <div className="input-custom d-flex align-items-center justify-content-center">
                <p className="mb-0 me-3">
                  Board Size <br />
                  Min 5 - Max 20 <br />
                </p>
                <TextField 
                  type="number"
                  required
                  onChange={(event) => {
                    this.setState({ numbercell: event.target.value });
                  }}
                  onKeyPress={(event) => {
                    if (event.key == "Enter") {
                      if (
                        this.state.numbercell == null ||
                        (!Number.isInteger(this.state.numbercell) &&
                          (parseInt(this.state.numbercell) < 5 ||
                            parseInt(this.state.numbercell) > 20))
                      ) {
                        alert("Invalid data!");
                        return;
                      }


                      setNumberCell(this.state.numbercell);
                    }
                  }}
                />
              </div>
            </>
          ) : (
            <div className="row board">
              <div className="col-md-10">
                {arrayBoard.map((e, index) => {
                  console.log("ðŸš€ ~ Home ~ {arrayBoard.map ~ e:", e)
                  return (
                    <div>
                      <Row
                        elements={e}
                        tick={(row, col) => tick(row, col)}
                        row={index}
                        piecesWin={piecesWin} />
                    </div>
                  );
                })}
              </div>
              <div className="col-md-2 information">
                <p className="title">INFORMATION</p>
                <p>ACTIVE: {currentPiece}</p>
                <p>
                  {" "}
                  Result :{" "}
                  {isWin == 1 ? (
                    <span>{currentPiece} WIN</span>
                  ) : isWin == 0 ? (
                    <span>DRAW</span>
                  ) : null}
                </p>
                <button onClick={() => resetMap()} className="btn btn-info">
                  Reset
                </button>
              </div>
            </div>
          )}
        {/* </div> */}
        </Container>
        </div>
        </>
    );
  }
}

export default Home;
