import React, {useState} from 'react';
import { TextField, Grid, Typography, Divider, ListItem, ListItemText, Dialog, DialogActions, DialogContent, DialogContentText, DialogTitle, Button } from "@material-ui/core";
import Wheel from './wheel'
import Card from './card'
import { recipes } from '../../constants/recipes';


const OverviewDialog = (props) => {
    const {
        dialogState = {},
        handleDialog,
        showAnswer,
        setShowSpin,
        setShowAnswer,
        showSpin,
        handlePlayerItems,
        player1Items,
        player2Items    
    } = props
    return (
        <Dialog
        fullWidth
        open={dialogState.open}
        onClose={() => {
          handleDialog({open: false })
          setShowAnswer(false)
          setShowSpin(false)
        }}
        aria-labelledby="max-width-dialog-title"
        maxWidth="md"
      >
         <DialogTitle id="max-width-dialog-title">Status</DialogTitle>
        <DialogContent style={{height: 200}}>
        <Grid container spacing={2} direction='row' justifyContent='space-around'>
          <Grid item sm={12} md={6} style={{textAlign: 'center'}}>
          <DialogContentText>
          <Typography variant="body1" >Player 1 - {player1Items.length} items</Typography>

          </DialogContentText>
          <Grid container spacing={2}>
          {player1Items.map((item, i) => {
              const {option} = item
              return (
                <Grid item>
                  {option}
                </Grid>
              )
          })}
           </Grid>
          <Divider orientation="vertical" flexItem style={{height: 400, color: 'black', opacity: 1}} />
          </Grid>
          <Grid item sm={12} md={6} style={{textAlign: 'center'}}>
          <DialogContentText>
          <Typography variant="body1" >Player 2 - {player2Items.length} items</Typography>
          </DialogContentText>
           <Grid container spacing={2}>
           {player2Items.map((item, i) => {
              const {option} = item
              return (
                <Grid item>
                  {option}
                </Grid>
              )
          })}
           </Grid>
          </Grid>
        </Grid>


           {/* <Grid container spacing={2}>
            {recipes.map((recipe, i) => {
              const {name, uri, ingredients} = recipe
              return (
                <Grid item key={i} xs={12} sm={6} md={2}>
                <Card
                  name={name}
                  uri={uri}
                  ingredients={ingredients}
                />
                </Grid>
              )
            })}
          </Grid> */}

        </DialogContent>
        <DialogActions>
          <Button onClick={() => {
            handleDialog({open: false}) 
            setShowAnswer(false)
            setShowSpin(false)
          }} 
            color="primary">
            Close
          </Button>
        </DialogActions>
      </Dialog>
    )
}

export default OverviewDialog
