import { useState } from "react";
import { types, pieces } from "../constants/action.types";

const initalState = {
  number_cell: null,
  array_board: null,
  piece_current: pieces.X
};


export const set_number_cell = number_cell => ({
    type: types.SET_NUMBER_CELL,
    number_cell
})

export const init_array = array_board => ({
    type: types.INIT_ARRAY,
    array_board
})

export const tick =  (array_new) => ({
    type: types.TICK,
    array_new
})

export const switch_piece = (data) => ({
    type: types.SWITCH_PIECE,
    data
})


export const useHomePage = () => {
  const [numberCell, setNumberCell] = useState(null)
  const [arrayBoard, setArrayBoard] = useState(null)
  const [switchPiece, setSwitchPiece] = useState(pieces.X)

  const main = (state = initalState, action) => {
    let updatedState = state
    switch (action.type) {
      case types.SET_NUMBER_CELL: {
        updatedState ={ ...state, number_cell: parseInt(action.number_cell) };
        break
      }
      case types.INIT_ARRAY: {
        updatedState ={ ...state, array_board: action.array_board };
        break
      }
      case types.SWITCH_PIECE: {
        updatedState ={ ...state, piece_current: action.data };
        break
      }
      case types.TICK: {
        updatedState ={...state, array_board: action.array_new}
        break
      }
      default:
        updatedState =state;
    }
  };

  const handleMainAction = (type, data) => {
    switch (type) {
      case types.SET_NUMBER_CELL: {
        setNumberCell({ number_cell: parseInt(data) })
        break
      }
      case types.INIT_ARRAY: {
        setArrayBoard({ array_board: data })
        break
      }
      case types.SWITCH_PIECE: {
        setSwitchPiece({ piece_current: data })
        break
      }
      case types.TICK: {
        setArrayBoard({array_board: data})
        break
      }
      default:
    }
  }


  return {
    numberCell,
    switchPiece,
    arrayBoard,
    main
  }
}
