import React, {useState} from 'react';
import AppBar from '@material-ui/core/AppBar';
import Grid from '@material-ui/core/Grid';
import Toolbar from '@material-ui/core/Toolbar';
import Typography from '@material-ui/core/Typography';
import { useStyles } from '../styles';
import Link from '@material-ui/core/Link';
import Header from "./common/header";
import Row from "./board/row";
import { TextField, Container, CssBaseline, Collapse, ListItem, ListItemText, Dialog, DialogActions, DialogContent, DialogContentText, DialogTitle, Button } from "@material-ui/core";
import Wheel from './common/wheel'
import {useHomePage} from '../hooks'
import SimpleDialog from './common/dialog';

function Copyright() {
  return (
    <Typography variant="body2" color="textSecondary" align="center">
      {'Copyright © '}
      <Link color="inherit" href="https://mui.com/">
        Your Website
      </Link>{' '}
      {new Date().getFullYear()}
      {'.'}
    </Typography>
  );
}



const Home = (props) =>  {
  const classes = useStyles();
  const [boardState, setBoardState] = useState({
      numbercell: null,
      show: false
  })

  const [showAnswer, setShowAnswer] = useState(false)
  const [showSpin, setShowSpin] = useState(false)

  const {
    currentPiece,
    arrayBoard,
    setNumberCell,
    onInitArray,
    tick,
    isWin,
    piecesWin,
    resetMap,
    handleDialog,
    dialogState,
    player1,
    player2,
    handleNameChange,
    player1Items,
    player2Items,
    handlePlayerItems
  } = useHomePage();
  console.log({    player1Items,
    player2Items,player1Items})

  return (
    <React.Fragment>
      <main className={classes.root}>
      <CssBaseline />
      <AppBar position="relative" color="transparent" style={{backgroundColor: '#fbd4d7'}}>
        <Toolbar>
        <Header 
         player1={player1}
         player2={player2}
         handleNameChange={handleNameChange}
        />
        </Toolbar>
      </AppBar>
        <div className={classes.heroContent}>
        <Container component="main" maxWidth="xlg" sx={{ mb: 4 }}>
           {arrayBoard === null ? (
            <>
              <div className="input-custom d-flex align-items-center justify-content-center">
                <TextField 
                  type="number"
                  required
                  label={"Board Size (5-20)"}
                  onChange={(event) => {
                    setBoardState({ ...boardState, numbercell: event.target.value });
                  }}
                  onKeyPress={(event) => {
                    if (event.key === "Enter") {
                      if (
                        boardState.numbercell === null ||
                        (!Number.isInteger(boardState.numbercell) &&
                          (parseInt(boardState.numbercell) < 5 ||
                            parseInt(boardState.numbercell) > 20))
                      ) {
                        alert("Invalid data!");
                        return;
                      }
                      setNumberCell(boardState.numbercell);
                    }
                  }}
                />
              </div>
            </>
          ) : (
            <Grid container direction="column" alignContent="center">
              <Grid item>
                <p className="title">INFORMATION</p>
                  <p>ACTIVE: {currentPiece}</p>
                  <p>
                    {" "}
                    Result :{" "}
                    {isWin === 1 ? (
                      <span>{currentPiece} WIN</span>
                    ) : isWin === 0 ? (
                      <span>DRAW</span>
                    ) : null}
                  </p>
                  <button onClick={() => resetMap()} className="btn btn-info">
                    Reset
                  </button>
              </Grid>
              <Grid item style={{border: '0.5px black solid', borderRadius: 5, backgroundColor: 'white'}}>
                {arrayBoard.map((e, index) => {
                    return (
                      <div >
                        <Row
                          elements={e}
                          tick={(row, col) => tick(row, col)}
                          row={index}
                          piecesWin={piecesWin} />
                      </div>
                    );
                  })}
              </Grid>
            </Grid>
          )}
        </Container>
        {/* <Wheel/> */}
        <SimpleDialog 
          dialogState={dialogState}
          handleDialog={handleDialog}
          showAnswer={showAnswer}
          setShowSpin={setShowSpin}
          setShowAnswer={setShowAnswer}
          showSpin={showSpin}
          handlePlayerItems={handlePlayerItems}    
        />
        {/* <Dialog
        fullWidth
        open={dialogState.open}
        onClose={() => {
          handleDialog({open: false })
          setShowAnswer(false)
          setShowSpin(false)
        }}
        aria-labelledby="max-width-dialog-title"
        maxWidth="md"
      >
         <DialogTitle id="max-width-dialog-title">{dialogState.title}</DialogTitle>
        <DialogContent>
          <ListItem button onClick={() => setShowAnswer(!showAnswer)}>
            <ListItemText primary={dialogState.content} />
          </ListItem> 
          <ListItem button onClick={() => setShowSpin(!showSpin)}>
            <Collapse in={showAnswer} timeout="auto" unmountOnExit>
              <ListItemText component="div" primary={dialogState.answer} />
            </Collapse>
          </ListItem >
          <ListItem  style={{justifyContent:'center'}}>
            <Collapse in={showSpin} timeout="auto" unmountOnExit>
              <Wheel
                handlePlayerItems={handlePlayerItems}
                currentPiece={dialogState.currentPiece}
              />
            </Collapse>
          </ListItem>
        </DialogContent>
        <DialogActions>
          <Button onClick={() => {
            handleDialog({open: false}) 
            setShowAnswer(false)
            setShowSpin(false)
          }} 
            color="primary">
            Close
          </Button>
        </DialogActions>
      </Dialog> */}
        </div>
      </main>
      {/* Footer */}
      <footer className={classes.footer}>
        <Typography variant="h6" align="center" gutterBottom>
          Footer
        </Typography>
        <Typography variant="subtitle1" align="center" color="textSecondary" component="p">
          Something here to give the footer a purpose!
        </Typography>
        <Copyright />
      </footer>
      {/* End footer */}
    </React.Fragment>
  );
}

export default Home
